      isValid = false;
      errorMessage = `âŒ DNI invÃ¡lido\n\nPor favor ingrese solo nÃºmeros, entre 7 y 8 dÃ­gitos.\n\nFormatos aceptados:\nâ€¢ 25430441\nâ€¢ 25.430.441\nâ€¢ 25 430 441\n\nSu entrada: "${messageText}"\nProcesado como: "${cleanDNI}"`;
