// Parse WhatsApp Message
const items = $input.all();
const message = items[0].json.body.data.message;

if (!message || message.type !== 'chat') {
  return [{ json: { error: "Invalid message type" } }];
}

const chatId = message.from;
const messageText = message.body?.trim() || "";
const messageId = message.id;

return {
  json: {
    chatId: chatId,
    messageText: messageText,
    messageId: messageId,
    timestamp: new Date().toISOString()
  }
};

// Process User State and Determine Next Step
const chatId = $('Parse Message').first().json.chatId;
const messageText = $('Parse Message').first().json.messageText.trim();
const token = $('Get Auth Token').first().json.token;
const especialidades = $('Get Especialidades').all();

// Get user session from JSONBin
let allSessions = {};
let userSession = null;

try {
  const jsonbinResponse = $('Get User Session').first().json;
  if (jsonbinResponse && jsonbinResponse.record && jsonbinResponse.record.sessions) {
    allSessions = jsonbinResponse.record.sessions;
    console.log('‚úÖ JSONBin data loaded, total users:', Object.keys(allSessions).length);
  } else {
    console.log('‚ö†Ô∏è No existing JSONBin data, creating new structure');
    allSessions = {};
  }
  userSession = allSessions[chatId];
} catch (error) {
  console.log('‚ùå Error reading JSONBin, creating new structure:', error.message);
  allSessions = {};
}

// Initialize new session if needed
const isNewUser = !userSession;
if (!userSession) {
  userSession = {
    chatId: chatId,
    estado: "INICIO",
    datos: {},
    createdAt: new Date().toISOString(),
    lastActivity: new Date().toISOString()
  };
  console.log('üÜï Created new user session for:', chatId);
} else {
  userSession.lastActivity = new Date().toISOString();
  console.log('üîÑ Updated existing user session for:', chatId);
}

// Process based on current state
let response = {};
let newState = userSession.estado;
let updateData = { ...userSession.datos };
let isValid = true;
let errorMessage = "";
let needsDniValidation = false;
let needsDaysData = false;
let needsHoursData = false;
let needsApiSubmission = false;

// CRITICAL FIX: Detect if we're transitioning from INICIO/COMPLETADO states
const currentState = userSession.estado;
let skipDniProcessing = (currentState === "INICIO" || currentState === "COMPLETADO");

// CRITICAL: If skipDniProcessing is true, we MUST prevent ALL processing nodes from executing
if (skipDniProcessing) {
  console.log('üõë SKIP MODE ACTIVE: Preventing all processing operations for state transition from ' + currentState);
  // Force all processing flags to false to prevent parallel execution of validation nodes
  needsDniValidation = false;
  needsDaysData = false;
  needsHoursData = false;
  needsApiSubmission = false;
}

switch (userSession.estado) {
  case "INICIO":
  case "COMPLETADO":
    // Start or restart flow - ONLY show welcome message
    if (userSession.estado === "COMPLETADO") {
      updateData = {}; // Clear previous data
    }
    response = {
      message: "üè• Bienvenido al Sistema de Turnos\n\nPor favor, indique su DNI (solo n√∫meros, sin puntos):"
    };
    newState = "ESPERANDO_DNI";
    console.log('üîÑ State transition: ' + currentState + ' -> ESPERANDO_DNI (skipDniProcessing: true)');
    break;

  case "ESPERANDO_DNI":
    // CRITICAL: Skip DNI processing if we just transitioned from INICIO/COMPLETADO
    if (skipDniProcessing) {
      console.log('‚è≠Ô∏è Skipping DNI processing - just transitioned from ' + currentState + ' state');
      // The user will need to send another message for actual DNI validation
      // Keep current response (welcome message) and don't process current message as DNI
      // DO NOT break here - we want to keep the welcome message from the previous case
      console.log('üìù Keeping welcome message, user needs to send DNI in next message');
      break;
    }

    // Process actual DNI input from user's next message
    console.log('üîç Processing DNI input: "' + messageText + '"');

    // Clean and validate DNI
    const cleanDNI = messageText.replace(/[^\d]/g, '');

    if (!cleanDNI || cleanDNI.length < 7 || cleanDNI.length > 8) {
      isValid = false;
      errorMessage = `‚ùå DNI inv√°lido\n\nPor favor ingrese solo n√∫meros, entre 7 y 8 d√≠gitos.\n\nFormatos aceptados:\n‚Ä¢ 25430441\n‚Ä¢ 25.430.441\n‚Ä¢ 25 430 441\n\nSu entrada: "${messageText}"\nProcesado como: "${cleanDNI}"`;
    } else {
      updateData.dni = cleanDNI;
      needsDniValidation = true;
      newState = "VALIDANDO_DNI";
      response = {
        message: "‚è≥ Validando DNI..."
      };
    }
    break;

  case "ESPERANDO_ESPECIALIDAD":
    const especialidadId = parseInt(messageText);
    const especialidad = especialidades.find(e => e.idEspecialidad === especialidadId);

    if (!especialidad) {
      isValid = false;
      let opciones = especialidades.map(e => `${e.idEspecialidad}. ${e.descripcion}`).join('\n');
      errorMessage = `‚ùå No se reconoce el valor ingresado\n\nOpciones v√°lidas:\n${opciones}`;
    } else {
      updateData.especialidad = especialidad;
      needsDaysData = true;
      newState = "CARGANDO_DIAS";
      response = {
        message: `‚è≥ Obteniendo d√≠as disponibles para ${especialidad.descripcion}...`
      };
    }
    break;

  case "ESPERANDO_DIA":
    const diaId = parseInt(messageText);
    if (!updateData.diasDisponibles || !updateData.diasDisponibles[diaId]) {
      isValid = false;
      if (updateData.diasDisponibles) {
        let opciones = Object.keys(updateData.diasDisponibles).map(key => `${key}. ${updateData.diasDisponibles[key]}`).join('\n');
        errorMessage = `‚ùå No se reconoce el valor ingresado\n\nOpciones v√°lidas:\n${opciones}`;
      } else {
        errorMessage = "‚ùå Error: D√≠as no cargados. Por favor inicie nuevamente el proceso.";
      }
    } else {
      updateData.diaSeleccionado = {
        id: diaId,
        descripcion: updateData.diasDisponibles[diaId]
      };
      needsHoursData = true;
      newState = "CARGANDO_HORARIOS";
      response = {
        message: `‚è≥ Obteniendo horarios disponibles para ${updateData.diaSeleccionado.descripcion}...`
      };
    }
    break;

  case "ESPERANDO_HORARIO":
    const horarioId = parseInt(messageText);
    if (!updateData.horariosDisponibles || !updateData.horariosDisponibles[horarioId]) {
      isValid = false;
      if (updateData.horariosDisponibles) {
        let opciones = Object.keys(updateData.horariosDisponibles).map(key => `${key}. ${updateData.horariosDisponibles[key]}`).join('\n');
        errorMessage = `‚ùå No se reconoce el valor ingresado\n\nOpciones v√°lidas:\n${opciones}`;
      } else {
        errorMessage = "‚ùå Error: Horarios no cargados. Por favor inicie nuevamente el proceso.";
      }
    } else {
      updateData.horarioSeleccionado = {
        id: horarioId,
        descripcion: updateData.horariosDisponibles[horarioId]
      };

      // Show confirmation
      const resumen = `üìã Resumen del Turno:\n\n` +
                     `üë§ DNI: ${updateData.dni}\n` +
                     `üë®‚Äç‚öïÔ∏è Especialidad: ${updateData.especialidad.descripcion}\n` +
                     `üìÖ D√≠a: ${updateData.diaSeleccionado.descripcion}\n` +
                     `üïê Horario: ${updateData.horarioSeleccionado.descripcion}\n\n` +
                     `¬øConfirma el turno?\n\n` +
                     `1. ‚úÖ Aceptar\n` +
                     `2. ‚ùå Cancelar`;

      response = {
        message: resumen
      };
      newState = "CONFIRMACION";
    }
    break;

  case "CONFIRMACION":
    const confirmacion = parseInt(messageText);

    if (confirmacion === 1) {
      needsApiSubmission = true;
      newState = "ENVIANDO_API";
      response = {
        message: "‚úÖ Turno confirmado\n\nProcesando su solicitud..."
      };
    } else if (confirmacion === 2) {
      updateData = {};
      newState = "INICIO";
      response = {
        message: "‚ùå Turno cancelado\n\n¬øDesea agendar un nuevo turno?\n\nEnv√≠e cualquier mensaje para comenzar."
      };
    } else {
      isValid = false;
      errorMessage = "‚ùå No se reconoce el valor ingresado\n\nOpciones v√°lidas:\n1. ‚úÖ Aceptar\n2. ‚ùå Cancelar";
    }
    break;

  default:
    // Unknown state, restart
    response = {
      message: "üè• Bienvenido al Sistema de Turnos\n\nPor favor, indique su DNI (solo n√∫meros, sin puntos):"
    };
    newState = "ESPERANDO_DNI";
    updateData = {};
}

// Update session data
allSessions[chatId] = {
  chatId: chatId,
  estado: isValid ? newState : userSession.estado,
  datos: isValid ? updateData : userSession.datos,
  createdAt: userSession.createdAt,
  lastActivity: new Date().toISOString()
};

// Clean message for WhatsApp
let finalMessage = isValid ? response.message : errorMessage;
let cleanedMessage = finalMessage
  .replace(/^#+\\s+(.+)$/gm, "$1")
  .replace(/\\*\\*([^*]+)\\*\\*/g, "$1")
  .replace(/^\\s*-\\s+/gm, "- ");

return {
  json: {
    chatId: chatId,
    isValid: isValid,
    response: {
      message: cleanedMessage
    },
    allSessions: allSessions,
    token: token,
    needsDniValidation: needsDniValidation,
    needsDaysData: needsDaysData,
    needsHoursData: needsHoursData,
    needsApiSubmission: needsApiSubmission,
    currentState: isValid ? newState : userSession.estado,
    timestamp: new Date().toISOString()
  }
};

// Process DNI Validation Result
const stateData = $('Process State Logic').first().json;
const chatId = stateData.chatId;
let allSessions = stateData.allSessions;

// Get especialidades with validation
let especialidades = [];
try {
  const especialidadesResponse = $('Get Especialidades').all();

  // Validate and normalize especialidades
  if (Array.isArray(especialidadesResponse)) {
    especialidades = especialidadesResponse;
  } else if (especialidadesResponse && typeof especialidadesResponse === 'object') {
    // If it's an object, try to extract array or handle as single item
    if (especialidadesResponse.data && Array.isArray(especialidadesResponse.data)) {
      especialidades = especialidadesResponse.data;
    } else if (especialidadesResponse.especialidades && Array.isArray(especialidadesResponse.especialidades)) {
      especialidades = especialidadesResponse.especialidades;
    } else if (especialidadesResponse.idEspecialidad) {
      // Single specialty object
      especialidades = [especialidadesResponse];
    }
  }

  console.log('‚úÖ Especialidades processed:', especialidades.length + ' items');
} catch (error) {
  console.log('‚ùå Error processing especialidades:', error.message);
  especialidades = [];
}

// Check if DNI validation was successful
let dniValid = false;
let errorMessage = "";

try {
  const dniResponse = $('Validate DNI').first().json;
  if (dniResponse && (dniResponse.idPaciente || dniResponse.id)) {
    dniValid = true;
    // Store patient data
    allSessions[chatId].datos.paciente = dniResponse;
  } else {
    errorMessage = "‚ùå DNI no encontrado en el sistema\n\nPor favor verifique el n√∫mero ingresado o comun√≠quese con la cl√≠nica para registrarse.";
  }
} catch (error) {
  console.log('DNI Validation Error:', error.message);
  errorMessage = "‚ùå Error validando DNI\n\nPor favor intente nuevamente o verifique el n√∫mero ingresado.";
}

let responseMessage = "";

if (dniValid) {
  // Show specialties
  allSessions[chatId].estado = "ESPERANDO_ESPECIALIDAD";

  let especialidadesText = "üë®‚Äç‚öïÔ∏è Seleccione una especialidad:\n\n";

  // Validate especialidades is an array before forEach
  if (Array.isArray(especialidades) && especialidades.length > 0) {
    especialidades.forEach(esp => {
      if (esp && esp.idEspecialidad && esp.descripcion) {
        especialidadesText += `${esp.idEspecialidad}. ${esp.descripcion}\n`;
      }
    });
  } else {
    console.log('‚ö†Ô∏è No especialidades available or invalid format');
    especialidadesText = "‚ùå No hay especialidades disponibles en este momento.\n\nPor favor intente m√°s tarde.";
    // Keep in DNI validation state to allow retry
    allSessions[chatId].estado = "ESPERANDO_DNI";
  }

  responseMessage = especialidadesText;
} else {
  // Keep in DNI state with error
  allSessions[chatId].estado = "ESPERANDO_DNI";
  responseMessage = errorMessage;
}

// Clean message for WhatsApp
let cleanedMessage = responseMessage
  .replace(/^#+\\s+(.+)$/gm, "$1")
  .replace(/\\*\\*([^*]+)\\*\\*/g, "$1")
  .replace(/^\\s*-\\s+/gm, "- ");

return {
  json: {
    chatId: chatId,
    response: {
      message: cleanedMessage
    },
    allSessions: allSessions,
    dniValid: dniValid,
    timestamp: new Date().toISOString()
  }
};

// Process Days Data
const stateData = $('Process State Logic').first().json;
const chatId = stateData.chatId;
let allSessions = stateData.allSessions;

// Get days from API response
const diasResponse = $('Get Days').first().json;
let diasObj = {};

// Process days - expecting array with idTipoDia and descripcion
if (Array.isArray(diasResponse)) {
  diasResponse.forEach(dia => {
    if (dia.idTipoDia && dia.descripcion) {
      diasObj[dia.idTipoDia] = dia.descripcion;
    }
  });
} else if (diasResponse && typeof diasResponse === 'object') {
  // If it's already an object, use it directly
  diasObj = diasResponse;
}

// Store days data in session
allSessions[chatId].datos.diasDisponibles = diasObj;
allSessions[chatId].estado = "ESPERANDO_DIA";

// Format days for display
let diasText = "üìÖ Seleccione un d√≠a:\n\n";
Object.keys(diasObj).forEach(key => {
  diasText += `${key}. ${diasObj[key]}\n`;
});

// Clean message for WhatsApp
let cleanedMessage = diasText
  .replace(/^#+\\s+(.+)$/gm, "$1")
  .replace(/\\*\\*([^*]+)\\*\\*/g, "$1")
  .replace(/^\\s*-\\s+/gm, "- ");

return {
  json: {
    chatId: chatId,
    response: {
      message: cleanedMessage
    },
    allSessions: allSessions,
    timestamp: new Date().toISOString()
  }
};

// Process Hours Data
const stateData = $('Process State Logic').first().json;
const chatId = stateData.chatId;
let allSessions = stateData.allSessions;

// Get hours from API response
const horariosResponse = $('Get Hours').first().json;
let horariosObj = {};

// Process hours - expecting array with idRangoHorario and descripcion
if (Array.isArray(horariosResponse)) {
  horariosResponse.forEach(horario => {
    if (horario.idRangoHorario && horario.descripcion) {
      horariosObj[horario.idRangoHorario] = horario.descripcion;
    }
  });
} else if (horariosResponse && typeof horariosResponse === 'object') {
  // If it's already an object, use it directly
  horariosObj = horariosResponse;
}

// Store hours data in session
allSessions[chatId].datos.horariosDisponibles = horariosObj;
allSessions[chatId].estado = "ESPERANDO_HORARIO";

// Format hours for display
let horariosText = "üïê Seleccione un horario:\n\n";
Object.keys(horariosObj).forEach(key => {
  horariosText += `${key}. ${horariosObj[key]}\n`;
});

// Clean message for WhatsApp
let cleanedMessage = horariosText
  .replace(/^#+\\s+(.+)$/gm, "$1")
  .replace(/\\*\\*([^*]+)\\*\\*/g, "$1")
  .replace(/^\\s*-\\s+/gm, "- ");

return {
  json: {
    chatId: chatId,
    response: {
      message: cleanedMessage
    },
    allSessions: allSessions,
    timestamp: new Date().toISOString()
  }
};

