// Process API Submission Result
const stateData = $('Process State Logic').first().json;
const chatId = stateData.chatId;
let allSessions = stateData.allSessions;

// Check API response
let success = false;
let responseMessage = "";

try {
  const apiResponse = $('Submit Turno').first().json;

  if (apiResponse && (apiResponse.success || apiResponse.id || apiResponse.idSolicitud)) {
    success = true;
    allSessions[chatId].estado = "COMPLETADO";

    const userData = allSessions[chatId].datos;
    responseMessage = `‚úÖ Turno solicitado exitosamente\n\n` +
                     `üìã Resumen:\n` +
                     `üë§ DNI: ${userData.dni}\n` +
                     `üë®‚Äç‚öïÔ∏è Especialidad: ${userData.especialidad.descripcion}\n` +
                     `üìÖ D√≠a: ${userData.diaSeleccionado.descripcion}\n` +
                     `üïê Horario: ${userData.horarioSeleccionado.descripcion}\n\n` +
                     `üìû La cl√≠nica se comunicar√° con usted para confirmar el turno.\n\n` +
                     `üîÑ Para solicitar otro turno, env√≠e cualquier mensaje.`;
  } else {
    responseMessage = "‚ùå Error al procesar la solicitud\n\nPor favor intente nuevamente m√°s tarde o comun√≠quese directamente con la cl√≠nica.";
    // Keep in confirmation state to allow retry
    allSessions[chatId].estado = "CONFIRMACION";
  }
} catch (error) {
  console.log('API Submission Error:', error.message);
  responseMessage = "‚ùå Error al procesar la solicitud\n\nPor favor intente nuevamente m√°s tarde o comun√≠quese directamente con la cl√≠nica.";
  allSessions[chatId].estado = "CONFIRMACION";
}

// Clean message for WhatsApp
let cleanedMessage = responseMessage
  .replace(/^#+\\s+(.+)$/gm, "$1")
  .replace(/\\*\\*([^*]+)\\*\\*/g, "$1")
  .replace(/^\\s*-\\s+/gm, "- ");

return {
  json: {
    chatId: chatId,
    response: {
      message: cleanedMessage
    },
    allSessions: allSessions,
    success: success,
    timestamp: new Date().toISOString()
  }
};

// Merge All Responses
const stateData = $('Process State Logic').first().json;

// Check if we have responses from other nodes
let finalResponse = stateData.response;
let finalAllSessions = stateData.allSessions;

// Check for DNI validation result
try {
  const dniResult = $('Process DNI Result').first()?.json;
  if (dniResult && dniResult.response) {
    finalResponse = dniResult.response;
    finalAllSessions = dniResult.allSessions;
  }
} catch (error) {
  // No DNI result, continue
}

// Check for days result
try {
  const daysResult = $('Process Days Result').first()?.json;
  if (daysResult && daysResult.response) {
    finalResponse = daysResult.response;
    finalAllSessions = daysResult.allSessions;
  }
} catch (error) {
  // No days result, continue
}

// Check for hours result
try {
  const hoursResult = $('Process Hours Result').first()?.json;
  if (hoursResult && hoursResult.response) {
    finalResponse = hoursResult.response;
    finalAllSessions = daysResult.allSessions;
  }
} catch (error) {
  // No hours result, continue
}

// Check for API submission result
try {
  const apiResult = $('Process API Result').first()?.json;
  if (apiResult && apiResult.response) {
    finalResponse = apiResult.response;
    finalAllSessions = apiResult.allSessions;
  }
} catch (error) {
  // No API result, continue
}

return {
  json: {
    chatId: stateData.chatId,
    response: finalResponse,
    allSessions: finalAllSessions,
    timestamp: new Date().toISOString()
  }
};

// Update Session - MANEJO DE USUARIOS NUEVOS
const mergeData = $('Merge Responses').first().json;
const chatId = mergeData.chatId;
const newState = mergeData.allSessions[chatId]?.estado;
const sessionData = mergeData.allSessions[chatId]?.datos;

// Obtener JSONBin actual o crear estructura vac√≠a si no existe
let allSessions = {};
try {
  // En este punto ya tenemos los datos procesados de mergeData
  allSessions = mergeData.allSessions;
  console.log('‚úÖ JSONBin data loaded, total users:', Object.keys(allSessions).length);
} catch (error) {
  console.log('‚ùå Error reading JSONBin, creating new structure:', error.message);
  allSessions = {};
}

// Crear o actualizar sesi√≥n del usuario
const isNewUser = !allSessions[chatId]?.createdAt;

if (allSessions[chatId]) {
  allSessions[chatId] = {
    chatId: chatId,
    estado: newState,
    datos: sessionData,
    createdAt: isNewUser ? new Date().toISOString() : (allSessions[chatId]?.createdAt || new Date().toISOString()),
    updatedAt: new Date().toISOString(),
    lastActivity: new Date().toISOString()
  };
}

console.log(isNewUser ? 'üÜï Created new user session' : 'üîÑ Updated existing user session');
console.log('User state:', newState);
console.log('Session data:', JSON.stringify(sessionData, null, 2));

// Preparar estructura completa para JSONBin
const jsonbinData = {
  sessions: allSessions
};

return {
  json: {
    chatId: chatId,
    jsonbinData: jsonbinData,
    timestamp: new Date().toISOString()
  }
};

// Prepare data for WhatsApp
const mergeData = $('Merge Responses').first().json;

return {
  json: {
    chatId: mergeData.chatId,
    message: mergeData.response.message
  }
};
