const messageText = $json.messageText.toLowerCase();
const userSession = $json.userSession;
const chatId = $json.chatId;
const maestros = $('Maestros').first().json;

let response = {};
let newEstado = userSession.estado;
let updateData = { ...userSession.datos };
let isValid = true;
let errorMessage = "";

// Funci√≥n auxiliar para parsear selecci√≥n m√∫ltiple
function parseMultipleSelection(input, validOptions) {
  console.log('=== PARSE MULTIPLE SELECTION ===');
  console.log('Input:', input);
  console.log('Valid options:', Object.keys(validOptions));

  if (!input || typeof input !== 'string') {
    console.log('Invalid input type');
    return {
      valid: false,
      selections: [],
      invalidItems: [input]
    };
  }

  const selections = input.split(',').map(item => item.trim()).filter(item => item.length > 0);
  const validSelections = [];
  const invalidItems = [];

  console.log('Parsed selections:', selections);

  for (const selection of selections) {
    if (validOptions.hasOwnProperty(selection)) {
      validSelections.push({
        id: selection,
        value: validOptions[selection]
      });
      console.log(`Valid selection: ${selection} -> ${validOptions[selection]}`);
    } else {
      invalidItems.push(selection);
      console.log(`Invalid selection: ${selection}`);
    }
  }

  const result = {
    valid: invalidItems.length === 0 && validSelections.length > 0,
    selections: validSelections,
    invalidItems: invalidItems
  };

  console.log('Final result:', JSON.stringify(result, null, 2));
  console.log('================================');

  return result;
}

switch (userSession.estado) {
  case "INICIO":
    // Cualquier mensaje inicia el proceso
    response = {
      message: "üè• *Bienvenido al Sistema de Turnos*\n\nPor favor, indique su DNI (solo n√∫meros, sin puntos):",
      nextStep: "ESPERANDO_DNI"
    };
    newEstado = "ESPERANDO_DNI";
    break;

  case "COMPLETADO":
    // NUEVO CASO: Usuario que ya complet√≥ un turno quiere hacer otro
    console.log('=== REINICIANDO USUARIO COMPLETADO ===');
    console.log('Usuario:', chatId);
    console.log('Estado anterior:', userSession.estado);
    console.log('Datos anteriores:', JSON.stringify(userSession.datos, null, 2));

    // Limpiar datos y reiniciar
    updateData = {};
    response = {
      message: "üè• *Bienvenido nuevamente al Sistema de Turnos*\n\n¬øDesea agendar un nuevo turno?\n\nPor favor, indique su DNI (solo n√∫meros, sin puntos):",
      nextStep: "ESPERANDO_DNI"
    };
    newEstado = "ESPERANDO_DNI";

    console.log('Nuevo estado:', newEstado);
    console.log('Datos limpiados:', JSON.stringify(updateData, null, 2));
    console.log('=====================================');
    break;

  case "ESPERANDO_DNI":
    const dniInput = $json.messageText.trim();

    console.log('=== VALIDACI√ìN DNI DEBUG ===');
    console.log('Input original:', JSON.stringify(dniInput));
    console.log('Input length:', dniInput.length);
    console.log('Input chars:', dniInput.split('').map(c => c.charCodeAt(0)));

    // Limpiar DNI: quitar espacios, puntos, comas, guiones
    const cleanDNI = dniInput
      .replace(/\s+/g, '')        // Quitar espacios
      .replace(/\./g, '')         // Quitar puntos
      .replace(/,/g, '')          // Quitar comas  
      .replace(/-/g, '')          // Quitar guiones
      .replace(/[^\d]/g, '');     // Quitar cualquier car√°cter que no sea d√≠gito

    console.log('DNI limpio:', cleanDNI);
    console.log('DNI length:', cleanDNI.length);
    console.log('Es solo n√∫meros:', /^\d+$/.test(cleanDNI));
    console.log('===========================');

    // Validar DNI limpio (solo n√∫meros, 7-8 d√≠gitos)
    if (!cleanDNI || cleanDNI.length < 7 || cleanDNI.length > 8 || !/^\d+$/.test(cleanDNI)) {
      isValid = false;
      errorMessage = `‚ùå *DNI inv√°lido*\n\nPor favor ingrese solo n√∫meros, entre 7 y 8 d√≠gitos.\n\nüìù *Formatos aceptados:*\n‚Ä¢ 25430441\n‚Ä¢ 25.430.441\n‚Ä¢ 25 430 441\n\n‚ö†Ô∏è *Su entrada:* "${dniInput}"\nüí° *Procesado como:* "${cleanDNI}"`;

      console.log('‚ùå DNI INV√ÅLIDO - Raz√≥n:', {
        empty: !cleanDNI,
        tooShort: cleanDNI.length < 7,
        tooLong: cleanDNI.length > 8,
        notNumbers: !/^\d+$/.test(cleanDNI),
        length: cleanDNI.length
      });
    } else {
      updateData.dni = cleanDNI; // Guardar DNI limpio

      console.log('‚úÖ DNI V√ÅLIDO:', cleanDNI);

      // Obtener especialidades del maestro
      const especialidades = maestros.especialidades;

      let especialidadesText = "üë®‚Äç‚öïÔ∏è *Seleccione una especialidad:*\n\n";
      especialidades.forEach(esp => {
        especialidadesText += `${esp.idEspecialidad}. ${esp.descripcion}\n`;
      });

      response = {
        message: especialidadesText,
        nextStep: "ESPERANDO_ESPECIALIDAD"
      };
      newEstado = "ESPERANDO_ESPECIALIDAD";
    }
    break;

  case "ESPERANDO_ESPECIALIDAD":
    const especialidadId = parseInt($json.messageText.trim());
    const especialidad = maestros.especialidades.find(e => e.idEspecialidad === especialidadId);

    if (!especialidad) {
      isValid = false;
      let opciones = maestros.especialidades.map(e => `${e.idEspecialidad}. ${e.descripcion}`).join('\n');
      errorMessage = `‚ùå *No se reconoce el valor ingresado*\n\nOpciones v√°lidas:\n${opciones}`;
    } else {
      updateData.especialidad = especialidad;

      // Convertir objeto d√≠as a array para mostrar
      const diasObj = maestros.dias;
      let diasText = "üìÖ *Seleccione uno o m√°s d√≠as:*\n\n";
      Object.keys(diasObj).forEach(key => {
        diasText += `${key}. ${diasObj[key]}\n`;
      });
      diasText += "\nüí° *Puede seleccionar m√∫ltiples opciones separ√°ndolas con comas*\n";
      diasText += "Ejemplo: 1,3,5";

      response = {
        message: diasText,
        nextStep: "ESPERANDO_DIA"
      };
      newEstado = "ESPERANDO_DIA";
    }
    break;

  case "ESPERANDO_DIA":
    const diasInput = $json.messageText.trim();
    const diasObj = maestros.dias;
    const diasResult = parseMultipleSelection(diasInput, diasObj);

    if (!diasResult.valid || diasResult.selections.length === 0) {
      isValid = false;
      let opciones = Object.keys(diasObj).map(key => `${key}. ${diasObj[key]}`).join('\n');
      let errorMsg = `‚ùå *No se reconoce el valor ingresado*\n\nOpciones v√°lidas:\n${opciones}\n\n`;
      if (diasResult.invalidItems.length > 0) {
        errorMsg += `Valores inv√°lidos: ${diasResult.invalidItems.join(', ')}\n`;
      }
      errorMsg += "üí° Puede seleccionar m√∫ltiples opciones separ√°ndolas con comas\nEjemplo: 1,3,5";
      errorMessage = errorMsg;
    } else {
      updateData.dias = diasResult.selections;

      console.log('=== DIAS SELECTION DEBUG ===');
      console.log('Input:', diasInput);
      console.log('Parsed result:', JSON.stringify(diasResult, null, 2));
      console.log('Stored in updateData.dias:', JSON.stringify(updateData.dias, null, 2));
      console.log('============================');

      // Convertir objeto rangos horarios a array para mostrar
      const horariosObj = maestros.rangosHorarios;
      let horariosText = "üïê *Seleccione uno o m√°s rangos de horario:*\n\n";
      Object.keys(horariosObj).forEach(key => {
        horariosText += `${key}. ${horariosObj[key]}\n`;
      });
      horariosText += "\nüí° *Puede seleccionar m√∫ltiples opciones separ√°ndolas con comas*\n";
      horariosText += "Ejemplo: 1,2,4";

      response = {
        message: horariosText,
        nextStep: "ESPERANDO_HORARIO"
      };
      newEstado = "ESPERANDO_HORARIO";
    }
    break;

  case "ESPERANDO_HORARIO":
    const horariosInput = $json.messageText.trim();
    const horariosObj = maestros.rangosHorarios;
    const horariosResult = parseMultipleSelection(horariosInput, horariosObj);

    if (!horariosResult.valid || horariosResult.selections.length === 0) {
      isValid = false;
      let opciones = Object.keys(horariosObj).map(key => `${key}. ${horariosObj[key]}`).join('\n');
      let errorMsg = `‚ùå *No se reconoce el valor ingresado*\n\nOpciones v√°lidas:\n${opciones}\n\n`;
      if (horariosResult.invalidItems.length > 0) {
        errorMsg += `Valores inv√°lidos: ${horariosResult.invalidItems.join(', ')}\n`;
      }
      errorMsg += "üí° Puede seleccionar m√∫ltiples opciones separ√°ndolas con comas\nEjemplo: 1,2,4";
      errorMessage = errorMsg;
    } else {
      updateData.horarios = horariosResult.selections;

      console.log('=== HORARIOS SELECTION DEBUG ===');
      console.log('Input:', horariosInput);
      console.log('Parsed result:', JSON.stringify(horariosResult, null, 2));
      console.log('Stored in updateData.horarios:', JSON.stringify(updateData.horarios, null, 2));
      console.log('================================');

      // Mostrar resumen y confirmaci√≥n con m√∫ltiples selecciones
      const diasSeleccionados = updateData.dias.map(d => d.value).join(', ');
      const horariosSeleccionados = updateData.horarios.map(h => h.value).join(', ');

      const resumen = `üìã *Resumen del Turno:*\n\n` +
                     `üë§ DNI: ${updateData.dni}\n` +
                     `üë®‚Äç‚öïÔ∏è Especialidad: ${updateData.especialidad.descripcion}\n` +
                     `üìÖ D√≠as: ${diasSeleccionados}\n` +
                     `üïê Horarios: ${horariosSeleccionados}\n\n` +
                     `*¬øConfirma el turno?*\n\n` +
                     `1. ‚úÖ Aceptar\n` +
                     `2. ‚ùå Cancelar`;

      response = {
        message: resumen,
        nextStep: "CONFIRMACION"
      };
      newEstado = "CONFIRMACION";
    }
    break;

  case "CONFIRMACION":
    const confirmacion = parseInt($json.messageText.trim());

    if (confirmacion === 1) {
      // Aceptar - preparar para env√≠o a API
      response = {
        message: "‚úÖ *Turno confirmado*\n\nProcesando su solicitud...",
        nextStep: "COMPLETADO",
        sendToAPI: true
      };
      newEstado = "COMPLETADO";
    } else if (confirmacion === 2) {
      // Cancelar - reiniciar proceso
      updateData = {};
      response = {
        message: "‚ùå *Turno cancelado*\n\n¬øDesea agendar un nuevo turno?\n\nEnv√≠e cualquier mensaje para comenzar.",
        nextStep: "INICIO"
      };
      newEstado = "INICIO";
    } else {
      isValid = false;
      errorMessage = "‚ùå *No se reconoce el valor ingresado*\n\nOpciones v√°lidas:\n1. ‚úÖ Aceptar\n2. ‚ùå Cancelar";
    }
    break;

  default:
    // Estado desconocido, reiniciar
    console.log('=== ESTADO DESCONOCIDO - REINICIANDO ===');
    console.log('Estado actual:', userSession.estado);
    console.log('======================================');

    response = {
      message: "üè• *Bienvenido al Sistema de Turnos*\n\nPor favor, indique su DNI (solo n√∫meros, sin puntos):",
      nextStep: "ESPERANDO_DNI"
    };
    newEstado = "ESPERANDO_DNI";
    updateData = {};
}

// APLICAR LIMPIEZA DE CARACTERES (CORREGIDA)
let finalMessage = isValid ? response.message : errorMessage;

// Limpiar caracteres especiales para WAAPI pero mantener saltos de l√≠nea
let cleanedResponse = finalMessage
  // Quitamos los encabezados #
  .replace(/^#+\s+(.+)$/gm, "$1")
  // Quitamos asteriscos de negrita
  .replace(/\*\*([^*]+)\*\*/g, "$1")
  // Simplificamos listados
  .replace(/^\s*-\s+/gm, "- ");

// NO aplicar JSON.stringify para mantener los saltos de l√≠nea
// cleanedResponse = JSON.stringify(cleanedResponse).slice(1, -1);

// Resultado final
return {
  json: {
    chatId: chatId,
    isValid: isValid,
    response: {
      message: cleanedResponse, // Mensaje limpio para WAAPI
      sendToAPI: response.sendToAPI || false
    },
    newEstado: isValid ? newEstado : userSession.estado,
    updateData: isValid ? updateData : userSession.datos,
    sendToAPI: response.sendToAPI || false,
    timestamp: new Date().toISOString()
  }
};

