// Nodo: Update User Session (CORREGIDO PARA JSONBIN)
const processData = $json;
const chatId = processData.chatId;
const newEstado = processData.newEstado;
const updateData = processData.updateData;

// Obtener las sesiones actuales desde JSONBin
let allSessions = {};
try {
  const jsonbinData = $('Read JSONBin').first().json;
  if (jsonbinData && jsonbinData.record) {
    allSessions = { ...jsonbinData.record };
  }
  console.log('=== UPDATE USER SESSION ===');
  console.log('ChatId a actualizar:', chatId);
  console.log('Nuevo estado:', newEstado);
  console.log('Datos a actualizar:', JSON.stringify(updateData, null, 2));
  console.log('Sesiones existentes:', Object.keys(allSessions).length);
} catch (error) {
  console.error('Error obteniendo sesiones de JSONBin:', error);
}

// Actualizar o crear la sesi√≥n del usuario
allSessions[chatId] = {
  chatId: chatId,
  estado: newEstado,
  datos: updateData,
  createdAt: allSessions[chatId]?.createdAt || new Date().toISOString(),
  lastActivity: new Date().toISOString(),
  updatedAt: new Date().toISOString()
};

console.log('=== SESI√ìN ACTUALIZADA ===');
console.log('Sesi√≥n del usuario:', JSON.stringify(allSessions[chatId], null, 2));
console.log('Total sesiones despu√©s de actualizar:', Object.keys(allSessions).length);
console.log('============================');

// Preparar estructura para JSONBin
const jsonbinPayload = {
  sessions: allSessions
};

return {
  json: jsonbinPayload
};

// Nodo: Prepare API Request (CON VALIDACI√ìN MEJORADA)
const processStepData = $('Process Step Logic').first().json;
const jsonbinData = $json;

console.log('=== PREPARE API REQUEST DEBUG ===');
console.log('Process Step Data:', JSON.stringify(processStepData, null, 2));
console.log('JSONBin Data keys:', Object.keys(jsonbinData));
console.log('Send to API from Process Step:', processStepData.sendToAPI);
console.log('================================');

// VALIDACI√ìN PRINCIPAL: Verificar si debe enviar a API desde Process Step Logic
if (!processStepData.sendToAPI) {
  console.log('‚ùå SKIPPING API - Process Step Logic says sendToAPI = false');
  return {
    json: {
      skipAPI: true,
      reason: "Process Step Logic indicates no API call needed",
      processStepData: processStepData
    }
  };
}

// Obtener datos de la sesi√≥n actualizada
let userSession = null;
let sendToAPI = false;

try {
  // Los datos vienen en record desde JSONBin
  if (jsonbinData.record) {
    const sessions = jsonbinData.record;

    // Buscar la sesi√≥n que coincida con el chatId del Process Step Logic
    const targetChatId = processStepData.chatId;

    if (sessions[targetChatId]) {
      userSession = sessions[targetChatId];

      // VALIDACI√ìN CR√çTICA: Solo enviar a API si est√° en estado COMPLETADO Y Process Step Logic lo confirma
      if (userSession.estado === "COMPLETADO" && processStepData.sendToAPI === true) {
        sendToAPI = true;
      }
    }
  }

  console.log('=== SESSION VALIDATION ===');
  console.log('Target ChatId:', processStepData.chatId);
  console.log('User session found:', !!userSession);
  console.log('User session state:', userSession?.estado);
  console.log('Send to API decision:', sendToAPI);
  console.log('========================');

} catch (error) {
  console.error('Error extrayendo datos de JSONBin:', error);
  return {
    json: {
      skipAPI: true,
      error: "Error extrayendo datos de JSONBin: " + error.message
    }
  };
}

// Verificar si debe enviar a API
if (!sendToAPI || !userSession || !userSession.datos || !userSession.datos.dni) {
  console.log('‚ùå SKIPPING API - Validation failed');
  console.log('sendToAPI:', sendToAPI);
  console.log('userSession exists:', !!userSession);
  console.log('userSession.datos exists:', !!userSession?.datos);
  console.log('DNI exists:', !!userSession?.datos?.dni);

  return {
    json: {
      skipAPI: true,
      message: "No se requiere env√≠o a API - validaci√≥n fallida",
      debugInfo: {
        sendToAPI,
        hasUserSession: !!userSession,
        hasUserData: !!userSession?.datos,
        hasDNI: !!userSession?.datos?.dni,
        userState: userSession?.estado
      }
    }
  };
}

// Preparar arrays de d√≠as y horarios desde las selecciones m√∫ltiples
let diasArray = [];
let horariosArray = [];

console.log('=== DEBUGGING SELECTION DATA ===');
console.log('userSession.datos:', JSON.stringify(userSession.datos, null, 2));
console.log('dias exists:', !!userSession.datos.dias);
console.log('horarios exists:', !!userSession.datos.horarios);

// Extraer d√≠as seleccionados con validaci√≥n robusta
if (userSession.datos.dias && Array.isArray(userSession.datos.dias)) {
  console.log('Processing multiple dias:', userSession.datos.dias);
  diasArray = userSession.datos.dias
    .filter(dia => dia && dia.id !== undefined && dia.id !== null)
    .map(dia => parseInt(dia.id));
} else if (userSession.datos.dia && userSession.datos.dia.id) {
  // Compatibilidad con versi√≥n anterior (selecci√≥n √∫nica)
  console.log('Processing single dia:', userSession.datos.dia);
  diasArray = [parseInt(userSession.datos.dia.id)];
}

// Extraer horarios seleccionados con validaci√≥n robusta
if (userSession.datos.horarios && Array.isArray(userSession.datos.horarios)) {
  console.log('Processing multiple horarios:', userSession.datos.horarios);
  horariosArray = userSession.datos.horarios
    .filter(horario => horario && horario.id !== undefined && horario.id !== null)
    .map(horario => parseInt(horario.id));
} else if (userSession.datos.horario && userSession.datos.horario.id) {
  // Compatibilidad con versi√≥n anterior (selecci√≥n √∫nica)
  console.log('Processing single horario:', userSession.datos.horario);
  horariosArray = [parseInt(userSession.datos.horario.id)];
}

console.log('Final diasArray:', diasArray);
console.log('Final horariosArray:', horariosArray);
console.log('===============================');

// Preparar datos para API seg√∫n formato requerido
const apiData = {
  documento: userSession.datos.dni,
  telefono: userSession.chatId.split('@')[0],
  idEspecialidad: userSession.datos.especialidad.idEspecialidad,
  dias: diasArray,
  rangosHorarios: horariosArray
};

console.log('=== API DATA PREPARED (VALIDATED) ===');
console.log('D√≠as seleccionados:', diasArray);
console.log('Horarios seleccionados:', horariosArray);
console.log('API Data:', JSON.stringify(apiData, null, 2));
console.log('====================================');

// Validar que tenemos al menos una selecci√≥n de cada
if (diasArray.length === 0 || horariosArray.length === 0) {
  console.error('‚ùå Error: No hay d√≠as u horarios seleccionados');
  return {
    json: {
      skipAPI: true,
      error: "Error: Faltan d√≠as u horarios seleccionados"
    }
  };
