      horariosCount: horariosArray.length,
      totalCombinations: diasArray.length * horariosArray.length
    }
  }
};

// Nodo: Format Final Response (CORREGIDO PARA SELECCI√ìN M√öLTIPLE)
const skipAPI = $json.skipAPI || false;
let finalMessage = "";
let chatId = "";

// Obtener chatId del flujo
try {
  chatId = $('menssageIn').first().json.chatId || $('Get User Session').first().json.chatId;
} catch {
  chatId = "unknown";
}

if (skipAPI) {
  // Si no se envi√≥ a API, obtener el mensaje del Process Step Logic
  try {
    const stepLogicResponse = $('Process Step Logic').first().json.response;
    finalMessage = stepLogicResponse ? stepLogicResponse.message : "Mensaje procesado correctamente";
  } catch {
    finalMessage = "Error al obtener respuesta del flujo";
  }
} else {
  // Respuesta de la API
  const apiResponse = $json;
  let userSession = null;

  // Obtener sesi√≥n de usuario
  try {
    userSession = $('Prepare API Request').first().json.userSession;
  } catch {
    userSession = null;
  }

  console.log('=== FORMAT FINAL RESPONSE ===');
  console.log('API Response:', JSON.stringify(apiResponse, null, 2));
  console.log('User Session:', userSession ? 'Found' : 'Not found');
  if (userSession) {
    console.log('User Session Data:', JSON.stringify(userSession.datos, null, 2));
  }
  console.log('============================');

  // NUEVA CONDICI√ìN: La API devuelve un array con el ID del turno
  if (apiResponse && (typeof apiResponse === 'number' || typeof apiResponse === 'string')) {
    const turnoId = apiResponse;

    if (userSession && userSession.datos) {
      // Construir mensaje con selecciones m√∫ltiples
      let diasText = 'N/A';
      let horariosText = 'N/A';

      // Manejar d√≠as (m√∫ltiples o √∫nico)
      if (userSession.datos.dias && Array.isArray(userSession.datos.dias)) {
        diasText = userSession.datos.dias.map(d => d.value).join(', ');
      } else if (userSession.datos.dia && userSession.datos.dia.nombre) {
        diasText = userSession.datos.dia.nombre;
      }

      // Manejar horarios (m√∫ltiples o √∫nico)
      if (userSession.datos.horarios && Array.isArray(userSession.datos.horarios)) {
        horariosText = userSession.datos.horarios.map(h => h.value).join(', ');
      } else if (userSession.datos.horario && userSession.datos.horario.rango) {
        horariosText = userSession.datos.horario.rango;
      }

      finalMessage = `‚úÖ ¬°Turno agendado exitosamente!\\n\\n` +
                     `üé´ N√∫mero de turno: ${turnoId}\\n` +
                     `üë§ DNI: ${userSession.datos.dni}\\n` +
                     `üìû Tel√©fono: ${userSession.chatId.split('@')[0]}\\n` +
                     `üë®‚Äç‚öïÔ∏è Especialidad: ${userSession.datos.especialidad ? userSession.datos.especialidad.descripcion : 'N/A'}\\n` +
                     `üìÖ D√≠as: ${diasText}\\n` +
                     `üïê Horarios: ${horariosText}\\n\\n` +
                     `üì± Guarde este n√∫mero de turno para futuras consultas.\\n\\n` +
                     `¬°Gracias por usar nuestro sistema!`;
    } else {
      finalMessage = `‚úÖ ¬°Turno agendado exitosamente!\\n\\nüé´ N√∫mero de turno: ${turnoId}`;
    }
  } else {
    // Si no es un ID v√°lido, es un error
    finalMessage = `‚ùå Error al agendar el turno\\n\\n` +
                   `Lo sentimos, hubo un problema al procesar su solicitud.\\n` +
                   `Por favor intente nuevamente m√°s tarde.\\n\\n` +
                   `Error: ${typeof apiResponse === 'string' ? apiResponse : JSON.stringify(apiResponse) || 'Error desconocido'}`;
  }
}

// APLICAR LIMPIEZA DE CARACTERES
let cleanedMessage = finalMessage
  .replace(/^#+\\s+(.+)$/gm, "$1")
  .replace(/\\*\\*([^*]+)\\*\\*/g, "$1")
  .replace(/^\\s*-\\s+/gm, "- ");

cleanedMessage = JSON.stringify(cleanedMessage).slice(1, -1);

return {
  json: {
    chatId: chatId,
    message: cleanedMessage,
    messageType: "text"
  }
};
